
public class ForEx1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		/*for문
		표현식
		for(초기화; 조건식; 증감식) {
			조건식이 참일 때 수행 될 문장들을 적는다
		}
		*/
		
		for (int i = 0; i < 5; i++) {
			System.out.println(i);
		}
		
		
		for (int i = 1; i <= 5; i++) {
			System.out.println(i);
		}
		
		
	}

}
