/*
 * 무조건 while
 * ===============
 * 4  - I can do it.
 * 3  - I can do it.
 * 2  - I can do it.
 * 1  - I can do it.
 * 0  - I can do it.
 *  
 *  
 * */
public class ForTest10 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String str = "I can do it.";
		int cnt = 4;
		
		while(cnt >= 0) {
			System.out.println( cnt + " - " + str );
			cnt--;
		}
		
	}
	
}

